import React, { useEffect } from "react";
import { useParams, NavLink } from "react-router-dom";
import { useSelector, useDispatch } from "react-redux";
import parse from "html-react-parser";
import { Row, Col, Spin, Card, List, Button, Typography, Input } from "antd";
import {
  LoadingOutlined,
  PlusOutlined,
  SearchOutlined,
} from "@ant-design/icons";
import { getDetailSubject, setEnrollModal } from "../../slices/subject";
import {
  getDiscussionBySubjectId,
  searchDiscussion,
} from "../../slices/discussion";
import { getUserById, getAllUsers } from "../../slices/user";
import { userRole } from "../../utils/constant";
import "./style.scss";
import EnrollCourseModal from "../../components/enrollCourseModal";
import Discussion from "../../components/discussion";

const { Meta } = Card;
const { Paragraph } = Typography;

const currentUser = JSON.parse(sessionStorage.getItem("user"));

const DetailSubjectPage = () => {
  const { subjectId } = useParams();
  // console.log(subjectId);

  const dispatch = useDispatch();

  const { detailSubject, getDetailLoading } = useSelector(
    (state) => state.subjects
  );

  const { discussion, getLoading } = useSelector((state) => state.discussion);

  const { user } = useSelector((state) => state.user);

  useEffect(() => {
    dispatch(getDetailSubject(subjectId))
      .then(() => {
        dispatch(getUserById(detailSubject.userId));
      })
      .catch((err) => console.log(err));
    dispatch(getDiscussionBySubjectId(subjectId));
    dispatch(getAllUsers());
  }, [dispatch, subjectId, detailSubject.userId]);

  // const lecturer = allUsers.find((element) => element.userId === subject)
  var checkEnroll = null;

  if (detailSubject.students) {
    checkEnroll = detailSubject.students.find(
      (element) => element.userId === currentUser.userId
    );
  }

  console.log(checkEnroll);

  const handleEnroll = () => {
    dispatch(setEnrollModal(true));
  };

  const handleSearch = (e) => {
    if (e.target.value) {
      dispatch(searchDiscussion({ id: subjectId, title: e.target.value }));
    } else {
      dispatch(getDiscussionBySubjectId(subjectId));
    }
  };

  return !getDetailLoading ? (
    <Row justify="center" gutter={[16, 0]}>
      <Col span={14}>
        {/* <Skeleton active loading={getDetailLoading}> */}
        <div className="detail-subject">
          <div style={{ padding: "10px 0" }}>
            <Row justify="start">
              <Col span={12}>
                <h1 style={{ margin: 0 }}>{detailSubject.subjectName}</h1>
                <p>{`@Lecturer: ${user.name}`}</p>
              </Col>
            </Row>

            {/* <Row justify="end">
                <Col span={4}>
                  <i>{`Posted on ${moment(detailBlog.postDate).format(
                    "MMM D YYYY"
                  )}`}</i>
                </Col>
              </Row> */}
            <Row justify="start" style={{ margin: "30px 0" }}>
              <Col span={22}>
                <p style={{ fontWeight: 700 }}>Course Description:</p>
                <Paragraph>
                  {parse(
                    typeof detailSubject.subjectDescription === "string"
                      ? detailSubject.subjectDescription
                      : ""
                  )}
                </Paragraph>
              </Col>
            </Row>

            {/* <Row gutter={[48, 48]}>
                  {detailSubject.students.map((student) => (
                    <Col span={6}>
                      <Card
                        cover={
                          <img
                            alt="example"
                            src="data:image/png;base64,"
                          />
                        }
                      >
                        <Meta
                          title={student.name}
                          description={student.email}
                        />
                      </Card>
                    </Col>
                  ))}
                </Row>
               */}
            <Row justify="start">
              <Col span={12}>
                <p style={{ fontWeight: 700 }}>Students: </p>
              </Col>
            </Row>
            <List
              grid={{ gutter: [48, 48], column: 4 }}
              dataSource={detailSubject.students}
              renderItem={(item) => (
                <List.Item>
                  <Card
                    cover={
                      item.image ? (
                        <img alt="avatar" src={item.image} height={200} />
                      ) : (
                        <img
                          alt="example"
                          src="data:image/png;base64,"
                        />
                      )
                    }
                  >
                    <Meta title={item.name} description={item.email} />
                  </Card>
                </List.Item>
              )}
            />
            {(currentUser.roleId !== userRole.COMPANY &&
              currentUser.userId === detailSubject.userId) ||
            checkEnroll ? (
              <>
                <Row justify="start">
                  <Col span={12}>
                    <p
                      style={{
                        fontWeight: 700,
                        fontSize: "24px",
                        margin: "30px 0 10px 0",
                      }}
                    >
                      Discussions:
                    </p>
                  </Col>
                </Row>
                <Row justify="start" gutter={[48, 0]}>
                  <Col>
                    <NavLink to={`/new-topic`} state={{ id: subjectId }}>
                      <Button
                        type="primary"
                        icon={<PlusOutlined />}
                        className="new-topic-btn"
                      >
                        New Topic
                      </Button>
                    </NavLink>
                  </Col>
                  <Col span={16}>
                    <Input
                      placeholder="Search discussion..."
                      className="search-discussion"
                      onPressEnter={handleSearch}
                      suffix={<SearchOutlined />}
                    />
                  </Col>
                </Row>
                {!getLoading ? (
                  <>
                    {currentUser.roleId === userRole.USER ? (
                      <List
                        grid={{ gutter: [48, 16], column: 1 }}
                        dataSource={discussion}
                        style={{
                          marginTop: 50,
                        }}
                        renderItem={(item) => (
                          <List.Item>
                            {item.status === 2 && (
                              <Discussion
                                key={item.discussionId}
                                discussion={item}
                              />
                            )}
                          </List.Item>
                        )}
                      />
                    ) : (
                      <List
                        grid={{ gutter: [48, 16], column: 1 }}
                        dataSource={discussion}
                        style={{
                          marginTop: 50,
                        }}
                        renderItem={(item) => (
                          <List.Item>
                            <Discussion
                              key={item.discussionId}
                              discussion={item}
                            />
                          </List.Item>
                        )}
                      />
                    )}
                  </>
                ) : (
                  <div className="course-loading">
                    <Spin
                      size="large"
                      tip="Loading..."
                      style={{ color: "gray" }}
                      indicator={<LoadingOutlined spin />}
                    />
                  </div>
                )}
              </>
            ) : null}
          </div>
        </div>
        {/* </Skeleton> */}
      </Col>
      {currentUser.userId !== detailSubject.userId && !checkEnroll ? (
        <Col span={5}>
          <div className="enroll-subject">
            <p>Get started with this course</p>
            <Button
              type="primary"
              disabled={
                currentUser.roleId === userRole.LECTURER ||
                currentUser.roleId === userRole.COMPANY
                  ? true
                  : false
              }
              onClick={handleEnroll}
            >
              Enroll Course
            </Button>
          </div>
          <EnrollCourseModal />
        </Col>
      ) : null}
    </Row>
  ) : (
    <div className="course-loading">
      <Spin
        size="large"
        tip="Loading..."
        style={{ color: "gray" }}
        indicator={<LoadingOutlined spin />}
      />
    </div>
  );
};

export default DetailSubjectPage;
